import TransactionBox from "./TransactionBox";
import axios from "axios";
import { useEffect, useState } from "react";

export default function TransactionDetail({ result }) {
  const [walletData, setWalletData] = useState(null);
  console.log(result);
  const get_address = async () => {
    const node_url = `http://27.96.130.87:9052/wallet/transactionById?id=${result._source.id}`;
    const wallet_request = await axios.get(node_url, {
      headers: {
        api_key: "hello",
      },
    });
    console.log("wallet request");
    console.log(wallet_request.data);
    setWalletData(wallet_request.data);
  };

  useEffect(() => {
    get_address();
  }, []);

  return (
    <div className="m-3">
      {result._id ? (
        <div key={result._id}>
          <div className="divide-y divide-slate-400/75">
            <TransactionBox
              title="Transaction ID"
              content={result._source.id}
            />

            <TransactionBox
              title="Data Inputs"
              content={JSON.stringify({ ...result._source.dataInputs })}
            />

            <div className="pt-6 md:p-8 md:text-left space-y-1">
              <div className="font-bold text-lg">
                <div className="text-slate-700 dark:text-slate-800">Inputs</div>
              </div>
              {result._source.inputs.map((input, index) => (
                <>
                  <TransactionBox title="boxId" content={input.boxId} />
                  <TransactionBox
                    title="spendingProof"
                    content={JSON.stringify({ ...input.spendingProof })}
                  />
                  <TransactionBox
                    title="address"
                    content={
                      walletData ? walletData.inputs[index].address : null
                    }
                  />
                </>
              ))}
            </div>

            <div className="pt-6 md:p-8 md:text-left space-y-1">
              <div className="font-bold text-lg">
                <div className="text-slate-700 dark:text-slate-800">
                  Outputs
                </div>
              </div>
              <div className="divide-y divide-slate-600/75 divide-dashed">
                {result._source.outputs.map((output, index) => (
                  <div key={index}>
                    <TransactionBox title="boxId" content={output.boxId} />
                    <TransactionBox title="value" content={output.value} />
                    <TransactionBox
                      title="ergoTree"
                      content={output.ergoTree}
                    />
                    <TransactionBox
                      title="creationHeight"
                      content={output.creationHeight}
                    />
                    <TransactionBox
                      title="transactionId"
                      content={output.transactionId}
                    />
                    <TransactionBox
                      title="address"
                      content={
                        walletData ? walletData.outputs[index].address : null
                      }
                    />
                  </div>
                ))}
              </div>
            </div>

            <TransactionBox title="Size" content={result._source.size} />
          </div>
        </div>
      ) : (
        <div className="m-3 text-lg">Block Not Exists</div>
      )}
    </div>
  );
}

